# Create function to split input to Cogntive Services Extractive Summarization into batches of 125000 characters max
def split_into_batches(input_string, max_chars_per_batch):
    current_batch = ""
    result = []
    for c in input_string:
        if len(current_batch) + 1 > max_chars_per_batch:
            result.append(current_batch)
            current_batch = c
        else:
            current_batch += c
    if current_batch:
        result.append(current_batch)
    return result
